mapper.update:
UPDATE ARTICLE SET ART_TITLE = #{art_title}, ART_CONTENT = #{art_content}, ART_TAG1 = #{art_tag1, jdbcType=VARCHAR},
						   ART_TAG2 = #{art_tag2, jdbcType=VARCHAR}, ART_TAG3 = #{art_tag3, jdbcType=VARCHAR},
						   ART_TAG4 = #{art_tag4, jdbcType=VARCHAR}, ART_TAG5 = #{art_tag5, jdbcType=VARCHAR}
WHERE ART_ID = #{art_id} AND BRD_ID = #{brd_id}

mapper.select:
SELECT * FROM (SELECT ROWNUM AS RN, J.* FROM (SELECT * FROM ( SELECT * FROM ARTICLE A JOIN ( SELECT C1.COMM_VALUE AS BRD_NAME, B.* FROM BOARD B JOIN COMM C1 ON BRD_ID IN COMM_ID )USING(BRD_ID) JOIN ( SELECT C2.COMM_VALUE AS GEN_NAME, M.* FROM MEMBER M JOIN COMM C2 ON MEM_GENDER IN COMM_ID )USING(MEM_ID) LEFT OUTER JOIN ( SELECT C3.COMM_VALUE AS STATUS_NAME, T.*, R.REG_NAME FROM TRADE T LEFT OUTER JOIN COMM C3 ON T.TRD_STATUS = C3.COMM_ID LEFT OUTER JOIN REGION R ON T.REG_ID = R.REG_ID )USING(TRD_ID) LEFT OUTER JOIN ( SELECT COUNT(*) AS REP_CNT, ART_ID, BRD_ID FROM REPLY WHERE ISDELETE = 0 GROUP BY(ART_ID, BRD_ID) )USING(BRD_ID, ART_ID) ) WHERE BRD_ID
BETWEEN #{brd_id} and #{brd_id}+600
= #{brd_id}
BETWEEN #{brd_id} and #{brd_id}+99
ORDER BY ART_REGDATE DESC) J) WHERE RN BETWEEN #{start} and #{end}

