mapper.select:
select 	j.*, m.*
from 	join j left join member m  on (j.mem_id = m.mem_id)
where 	trd_id = #{trd_id}

select 	w.*, m.*
from 	Waiting w left join member m  on (w.mem_id = m.mem_id)
where 	trd_id = #{trd_id}

mapper.delete:
	DELETE From waiting
	WHERE trd_id   = #{trd_id}
	AND   mem_id   = #{mem_id}

DELETE From Join
WHERE trd_id 	= #{trd_id}
AND mem_id 		= #{mem_id}

		DELETE From favorite
		WHERE art_id   = #{art_id}
		AND   brd_id   = #{brd_id}
        AND   mem_id   = #{mem_id}

