mapper.select:
SELECT * FROM ( SELECT ROWNUM RN, TT.* FROM ( SELECT A.ART_ID , A.BRD_ID , A.ART_TITLE , A.MEM_ID , A.ART_REGDATE , A.ART_CONTENT , A.ART_TAG1 , A.ART_TAG2 , A.ART_TAG3 , A.ART_TAG4 , A.ART_TAG5 , A.TRD_ID , A.ART_GOOD , A.ART_BAD , A.ART_READ , A.ART_ISNOTICE , A.ISDELETE , A.REPORT_ID , M.MEM_USERNAME , M.MEM_PASSWORD , M.MEM_NICKNAME , M.MEM_EMAIL , M.MEM_TEL , M.MEM_REGION1 , M.MEM_REGION2 , M.MEM_AUTHORITY , M.MEM_REGDATE , M.MEM_IMAGE , M.MEM_GENDER , M.MEM_NAME , M.MEM_BIRTHDAY , M.MEM_LATEST , C1.COMM_ID as C1_COMM_ID , C1.COMM_VALUE as status_name , C2.comm_value as brd_name , R.REG_ID , R.REG_NAME , R.REG_PARENT , T.TRD_STATUS , T.TRD_MAX , T.TRD_ENDDATE , T.TRD_COST , T.TRD_LOC , T.TRD_MAXAGE , TRUNC(SYSDATE - A.ART_REGDATE) AS REST_REGDATE , TO_CHAR(T.TRD_ENDDATE, 'YYYY-MM-DD') AS TRD_FINISH , favorite_count(a.art_id, a.brd_id) AS favoriteCount , REP_CNT(brd_id, art_id) AS reply_count FROM ARTICLE A LEFT OUTER JOIN TRADE T ON(A.TRD_ID = T.TRD_ID) LEFT OUTER JOIN MEMBER M ON(A.MEM_ID = M.MEM_ID) LEFT OUTER JOIN COMM C1 ON(T.TRD_STATUS = C1.COMM_ID) LEFT OUTER JOIN COMM C2 ON C2.comm_id = a.brd_id LEFT OUTER JOIN REGION R ON(T.REG_ID = R.REG_ID)
WHERE A.BRD_ID BETWEEN 1100 AND 1200
WHERE A.BRD_ID = #{brd_id}
AND A.ISDELETE = 0 ORDER BY A.ART_REGDATE DESC) TT) WHERE RN BETWEEN #{start} AND #{end}

