mapper.update:
UPDATE	MESSAGE
SET		MES_STATUS = #{mes_status}
WHERE	MES_ID = #{mes_id}
AND 	MEM_SENDER_ID = #{mem_sender_id}
AND		MEM_RECEIVER_ID = #{mem_receiver_id}

UPDATE	MESSAGE
SET		ISDELETE = 1
WHERE	MES_ID = #{mes_id}
AND 	MEM_SENDER_ID = #{mem_sender_id}
AND		MEM_RECEIVER_ID = #{mem_receiver_id}

UPDATE	MESSAGE
SET		MES_ISREAD = 1
WHERE	MES_ID = #{mes_id}
AND 	MEM_SENDER_ID = #{mem_sender_id}
AND		MEM_RECEIVER_ID = #{mem_receiver_id}
