mapper.update:
UPDATE 	TRADE
SET 	TRD_STATUS = #{trd_status}
WHERE	TRD_ID = #{trd_id}

mapper.select:
SELECT * FROM (SELECT T.*, M.MEM_NICKNAME, A.ART_ID, A.BRD_ID, A.ART_TITLE, A.ART_REGDATE, A.ART_CONTENT, A.ART_TAG1, A.ART_TAG2, A.ART_TAG3, A.ART_TAG4, A.ART_TAG5, C1.COMM_VALUE TRD_STATUSNAME, C2.COMM_VALUE TRD_GENDERNAME, R.REG_NAME, J1.JOIN_COUNT FROM TRADE T LEFT OUTER JOIN ARTICLE A ON (T.TRD_ID = A.TRD_ID) LEFT OUTER JOIN MEMBER M ON (T.MEM_ID = M.MEM_ID) LEFT OUTER JOIN COMM C1 ON (T.TRD_STATUS = C1.COMM_ID) LEFT OUTER JOIN COMM C2 ON (T.TRD_GENDER = C2.COMM_ID) LEFT OUTER JOIN REGION R ON (T.REG_ID = R.REG_ID) LEFT OUTER JOIN (SELECT TRD_ID, COUNT(*) JOIN_COUNT FROM JOIN GROUP BY TRD_ID) J1 ON (T.TRD_ID = J1.TRD_ID) WHERE T.MEM_ID = #{mem_id}
AND T.TRD_STATUS = #{trd_status}
ORDER BY A.ART_REGDATE DESC)
WHERE ROWNUM <= 10

SELECT * FROM (SELECT T.*, M.MEM_NICKNAME, A.ART_ID, A.BRD_ID, A.ART_TITLE, A.ART_REGDATE, A.ART_CONTENT, A.ART_TAG1, A.ART_TAG2, A.ART_TAG3, A.ART_TAG4, A.ART_TAG5, C1.COMM_VALUE TRD_STATUSNAME, C2.COMM_VALUE TRD_GENDERNAME, R.REG_NAME, W.WAIT_DATE, J1.JOIN_COUNT FROM TRADE T LEFT OUTER JOIN ARTICLE A ON (T.TRD_ID = A.TRD_ID) LEFT OUTER JOIN MEMBER M ON (T.MEM_ID = M.MEM_ID) LEFT OUTER JOIN COMM C1 ON (T.TRD_STATUS = C1.COMM_ID) LEFT OUTER JOIN COMM C2 ON (T.TRD_GENDER = C2.COMM_ID) LEFT OUTER JOIN REGION R ON (T.REG_ID = R.REG_ID) LEFT OUTER JOIN WAITING W ON (T.TRD_ID = W.TRD_ID) LEFT OUTER JOIN (SELECT TRD_ID, COUNT(*) JOIN_COUNT FROM JOIN GROUP BY TRD_ID) J1 ON (T.TRD_ID = J1.TRD_ID) WHERE W.MEM_ID = #{mem_id}
AND T.TRD_STATUS = #{trd_status}
ORDER BY A.ART_REGDATE DESC)
WHERE ROWNUM <= 10

SELECT * FROM (SELECT T.*, M.MEM_NICKNAME, A.ART_ID, A.BRD_ID, A.ART_TITLE, A.ART_REGDATE, A.ART_CONTENT, A.ART_TAG1, A.ART_TAG2, A.ART_TAG3, A.ART_TAG4, A.ART_TAG5, C1.COMM_VALUE TRD_STATUSNAME, C2.COMM_VALUE TRD_GENDERNAME, R.REG_NAME, J.JOIN_DATE, J1.JOIN_COUNT FROM TRADE T LEFT OUTER JOIN ARTICLE A ON (T.TRD_ID = A.TRD_ID) LEFT OUTER JOIN MEMBER M ON (T.MEM_ID = M.MEM_ID) LEFT OUTER JOIN COMM C1 ON (T.TRD_STATUS = C1.COMM_ID) LEFT OUTER JOIN COMM C2 ON (T.TRD_GENDER = C2.COMM_ID) LEFT OUTER JOIN REGION R ON (T.REG_ID = R.REG_ID) LEFT OUTER JOIN JOIN J ON (T.TRD_ID = J.TRD_ID) LEFT OUTER JOIN (SELECT TRD_ID, COUNT(*) JOIN_COUNT FROM JOIN GROUP BY TRD_ID) J1 ON (T.TRD_ID = J1.TRD_ID) WHERE J.MEM_ID = #{mem_id}
AND T.TRD_STATUS = #{trd_status}
ORDER BY A.ART_REGDATE DESC)
WHERE ROWNUM <= 10
